def binary_search(arr, target):
    left, right = 0, len(arr) - 1

    while left &lt;= right:
        mid = (left + right) // 2
        if arr[mid] == target:
            return mid
        elif arr[mid] &lt; target:
            left = mid + 1
        else:
            right = mid - 1

    return -1  # 타겟을 찾지 못한 경우

